// Comprehensive Sales Report Analysis
const salesData = $input.all()[0].json;
const topProducts = $input.all()[1].json;
const newCustomers = $input.all()[2].json;

// Sales metrics
const todaySales = salesData.today || {};
const yesterdaySales = salesData.yesterday || {};
const thisWeekSales = salesData.thisWeek || {};
const thisMonthSales = salesData.thisMonth || {};

// Calculate growth percentages
function calculateGrowth(current, previous) {
  if (!previous || previous === 0) return current > 0 ? 100 : 0;
  return ((current - previous) / previous * 100).toFixed(1);
}

// Performance analysis
const performance = {
  dailyRevenue: {
    today: todaySales.revenue || 0,
    yesterday: yesterdaySales.revenue || 0,
    growth: calculateGrowth(todaySales.revenue || 0, yesterdaySales.revenue || 0)
  },
  dailyOrders: {
    today: todaySales.orderCount || 0,
    yesterday: yesterdaySales.orderCount || 0,
    growth: calculateGrowth(todaySales.orderCount || 0, yesterdaySales.orderCount || 0)
  },
  averageOrderValue: {
    today: todaySales.averageOrderValue || 0,
    yesterday: yesterdaySales.averageOrderValue || 0,
    growth: calculateGrowth(todaySales.averageOrderValue || 0, yesterdaySales.averageOrderValue || 0)
  },
  newCustomers: {
    today: newCustomers.count || 0,
    growth: calculateGrowth(newCustomers.count || 0, newCustomers.previousDay || 0)
  }
};

// Top products summary
const topProductsSummary = topProducts.products ? topProducts.products.slice(0, 5).map(product => ({
  name: product.name,
  sales: product.unitsSold,
  revenue: product.revenue
})) : [];

// Weekly and monthly progress
const weeklyProgress = {
  revenue: thisWeekSales.revenue || 0,
  orders: thisWeekSales.orderCount || 0,
  target: thisWeekSales.target || 0,
  progressPercent: thisWeekSales.target ? ((thisWeekSales.revenue / thisWeekSales.target) * 100).toFixed(1) : 0
};

const monthlyProgress = {
  revenue: thisMonthSales.revenue || 0,
  orders: thisMonthSales.orderCount || 0,
  target: thisMonthSales.target || 0,
  progressPercent: thisMonthSales.target ? ((thisMonthSales.revenue / thisMonthSales.target) * 100).toFixed(1) : 0
};

// Generate insights
const insights = [];

if (performance.dailyRevenue.growth > 10) {
  insights.push(`üìà Starker Umsatzanstieg: +${performance.dailyRevenue.growth}%`);
} else if (performance.dailyRevenue.growth < -10) {
  insights.push(`üìâ Umsatzr√ºckgang: ${performance.dailyRevenue.growth}%`);
}

if (performance.newCustomers.today > 5) {
  insights.push(`üë• Guter Kundenzuwachs: ${performance.newCustomers.today} neue Kunden`);
}

if (topProductsSummary.length > 0 && topProductsSummary[0].sales > 10) {
  insights.push(`üèÜ Top-Verk√§ufer: ${topProductsSummary[0].name} (${topProductsSummary[0].sales} St√ºck)`);
}

if (weeklyProgress.progressPercent > 100) {
  insights.push(`üéØ Wochenziel √ºbertroffen: ${weeklyProgress.progressPercent}%`);
} else if (weeklyProgress.progressPercent < 50) {
  insights.push(`‚ö†Ô∏è Wochenziel gef√§hrdet: ${weeklyProgress.progressPercent}%`);
}

return {
  reportDate: new Date().toLocaleDateString('de-DE'),
  performance,
  topProducts: topProductsSummary,
  weeklyProgress,
  monthlyProgress,
  insights,
  summary: {
    totalRevenue: performance.dailyRevenue.today,
    totalOrders: performance.dailyOrders.today,
    newCustomers: performance.newCustomers.today,
    topProductName: topProductsSummary.length > 0 ? topProductsSummary[0].name : 'Keine Verk√§ufe'
  }
};

