// Comprehensive Weekly Business Analysis
const salesData = $input.all()[1].json;
const customerData = $input.all()[2].json;
const productData = $input.all()[3].json;
const marketingData = $input.all()[4].json;
const weekRanges = $input.all()[0].json.weekRanges;

// Calculate growth percentages
function calculateGrowth(current, previous) {
  if (!previous || previous === 0) return current > 0 ? 100 : 0;
  return ((current - previous) / previous * 100).toFixed(1);
}

// Sales Performance Analysis
const salesPerformance = {
  revenue: {
    current: salesData.revenue || 0,
    previous: salesData.previousWeekRevenue || 0,
    growth: calculateGrowth(salesData.revenue || 0, salesData.previousWeekRevenue || 0),
    target: salesData.weeklyTarget || 0,
    achievement: salesData.weeklyTarget ? ((salesData.revenue / salesData.weeklyTarget) * 100).toFixed(1) : 0
  },
  orders: {
    current: salesData.orderCount || 0,
    previous: salesData.previousWeekOrders || 0,
    growth: calculateGrowth(salesData.orderCount || 0, salesData.previousWeekOrders || 0),
    averageValue: salesData.averageOrderValue || 0
  },
  conversion: {
    rate: salesData.conversionRate || 0,
    previous: salesData.previousConversionRate || 0,
    growth: calculateGrowth(salesData.conversionRate || 0, salesData.previousConversionRate || 0)
  }
};

// Customer Analytics
const customerAnalytics = {
  newCustomers: customerData.newCustomers || 0,
  returningCustomers: customerData.returningCustomers || 0,
  customerRetentionRate: customerData.retentionRate || 0,
  averageLifetimeValue: customerData.averageLTV || 0,
  segments: customerData.segments || [],
  churnRate: customerData.churnRate || 0
};

// Product Performance
const productPerformance = {
  topProducts: productData.topProducts || [],
  categoryPerformance: productData.categories || [],
  newProductSales: productData.newProductSales || 0,
  returnRate: productData.returnRate || 0,
  inventoryTurns: productData.inventoryTurns || 0
};

// Marketing Performance
const marketingPerformance = {
  websiteTraffic: marketingData.traffic || 0,
  trafficGrowth: calculateGrowth(marketingData.traffic || 0, marketingData.previousTraffic || 0),
  channels: marketingData.channels || [],
  newsletterMetrics: {
    subscribers: marketingData.newsletterSubscribers || 0,
    openRate: marketingData.openRate || 0,
    clickRate: marketingData.clickRate || 0
  },
  socialMedia: marketingData.socialMedia || {}
};

// Key Performance Indicators
const kpis = {
  totalRevenue: salesPerformance.revenue.current,
  revenueGrowth: salesPerformance.revenue.growth,
  customerAcquisitionCost: marketingData.acquisitionCost || 0,
  customerLifetimeValue: customerAnalytics.averageLifetimeValue,
  profitMargin: salesData.profitMargin || 0,
  inventoryDays: productData.averageInventoryDays || 0
};

// Business Insights Generation
const insights = [];

// Revenue Insights
if (parseFloat(salesPerformance.revenue.growth) > 15) {
  insights.push(`üìà Starkes Umsatzwachstum: +${salesPerformance.revenue.growth}% vs. Vorwoche`);
} else if (parseFloat(salesPerformance.revenue.growth) < -10) {
  insights.push(`üìâ Umsatzr√ºckgang: ${salesPerformance.revenue.growth}% - Ma√ünahmen erforderlich`);
}

// Customer Insights
if (customerAnalytics.newCustomers > customerAnalytics.returningCustomers) {
  insights.push(`üë• Hohes Neukunden-Wachstum: ${customerAnalytics.newCustomers} neue Kunden`);
}

if (customerAnalytics.churnRate > 5) {
  insights.push(`‚ö†Ô∏è Erh√∂hte Abwanderungsrate: ${customerAnalytics.churnRate}%`);
}

// Product Insights
if (productPerformance.returnRate > 10) {
  insights.push(`üì¶ Hohe Retourenquote: ${productPerformance.returnRate}% - Qualit√§t pr√ºfen`);
}

// Marketing Insights
if (parseFloat(marketingPerformance.trafficGrowth) > 20) {
  insights.push(`üåê Starker Traffic-Anstieg: +${marketingPerformance.trafficGrowth}%`);
}

// Recommendations
const recommendations = [];

if (parseFloat(salesPerformance.revenue.achievement) < 80) {
  recommendations.push('üéØ Wochenziel gef√§hrdet - Marketing-Kampagne verst√§rken');
}

if (customerAnalytics.customerRetentionRate < 70) {
  recommendations.push('üíù Customer Retention Programme implementieren');
}

if (productPerformance.inventoryTurns < 4) {
  recommendations.push('üì¶ Lagerbestand optimieren - langsame Artikel identifizieren');
}

return {
  weeklyReport: {
    reportPeriod: {
      weekNumber: weekRanges.currentWeekNumber - 1,
      year: weekRanges.currentYear,
      startDate: weekRanges.lastWeekStart,
      endDate: weekRanges.lastWeekEnd
    },
    salesPerformance,
    customerAnalytics,
    productPerformance,
    marketingPerformance,
    kpis,
    insights,
    recommendations,
    generatedAt: new Date().toISOString()
  }
};

// Generate Comprehensive Weekly Report Message
const report = $json.weeklyReport;

let message = `üìä **W√ñCHENTLICHER BUSINESS REPORT** üìä\n`;
message += `üóìÔ∏è **KW ${report.reportPeriod.weekNumber}/${report.reportPeriod.year}** (${DateTime.fromISO(report.reportPeriod.startDate).toFormat('dd.MM')} - ${DateTime.fromISO(report.reportPeriod.endDate).toFormat('dd.MM.yyyy')})\n\n`;

// Sales Performance
message += `üí∞ **SALES PERFORMANCE**\n`;
message += `‚îú Umsatz: **${report.salesPerformance.revenue.current.toFixed(2)}‚Ç¨**\n`;
message += `‚îú Wachstum: ${report.salesPerformance.revenue.growth > 0 ? 'üìà' : 'üìâ'} **${report.salesPerformance.revenue.growth}%**\n`;
message += `‚îú Ziel-Erreichung: **${report.salesPerformance.revenue.achievement}%**\n`;
message += `‚îú Bestellungen: **${report.salesPerformance.orders.current}** (+${report.salesPerformance.orders.growth}%)\n`;
message += `‚îî √ò Bestellwert: **${report.salesPerformance.orders.averageValue.toFixed(2)}‚Ç¨**\n\n`;

// Customer Analytics
message += `üë• **CUSTOMER ANALYTICS**\n`;
message += `‚îú Neue Kunden: **${report.customerAnalytics.newCustomers}**\n`;
message += `‚îú Wiederkehrende: **${report.customerAnalytics.returningCustomers}**\n`;
message += `‚îú Retention Rate: **${report.customerAnalytics.customerRetentionRate}%**\n`;
message += `‚îî √ò Lifetime Value: **${report.customerAnalytics.averageLifetimeValue.toFixed(2)}‚Ç¨**\n\n`;

// Top Products
if (report.productPerformance.topProducts.length > 0) {
  message += `üèÜ **TOP PRODUKTE**\n`;
  report.productPerformance.topProducts.slice(0, 3).forEach((product, index) => {
    const emoji = index === 0 ? 'ü•á' : index === 1 ? 'ü•à' : 'ü•â';
    message += `${emoji} ${product.name}: **${product.sales}** (${product.revenue.toFixed(2)}‚Ç¨)\n`;
  });
  message += `\n`;
}

// Marketing Performance
message += `üìà **MARKETING PERFORMANCE**\n`;
message += `‚îú Website Traffic: **${report.marketingPerformance.websiteTraffic.toLocaleString()}** (+${report.marketingPerformance.trafficGrowth}%)\n`;
message += `‚îú Newsletter Abonnenten: **${report.marketingPerformance.newsletterMetrics.subscribers}**\n`;
message += `‚îú √ñffnungsrate: **${report.marketingPerformance.newsletterMetrics.openRate}%**\n`;
message += `‚îî Conversion Rate: **${report.salesPerformance.conversion.rate}%**\n\n`;

// Key Insights
if (report.insights.length > 0) {
  message += `üí° **KEY INSIGHTS**\n`;
  report.insights.forEach(insight => {
    message += `‚Ä¢ ${insight}\n`;
  });
  message += `\n`;
}

// Recommendations
if (report.recommendations.length > 0) {
  message += `üéØ **HANDLUNGSEMPFEHLUNGEN**\n`;
  report.recommendations.forEach(rec => {
    message += `‚Ä¢ ${rec}\n`;
  });
  message += `\n`;
}

message += `üìä [Vollst√§ndiger Report](${$env.DRESSFORP_FRONTEND_URL}/admin/analytics/weekly)\n\n`;
message += `*W√∂chentlicher Business Intelligence Report*`;

return { weeklyMessage: message };
