// Comprehensive Monthly Business Intelligence Analysis
const salesData = $input.all()[1].json;
const cohortData = $input.all()[2].json;
const productData = $input.all()[3].json;
const financialData = $input.all()[4].json;
const marketData = $input.all()[5].json;
const monthRanges = $input.all()[0].json.monthRanges;

// Advanced growth calculation with trend analysis
function calculateGrowth(current, previous, trend = 'neutral') {
  if (!previous || previous === 0) return current > 0 ? 100 : 0;
  const growth = ((current - previous) / previous * 100);
  return {
    percentage: growth.toFixed(1),
    absolute: (current - previous).toFixed(2),
    trend: growth > 5 ? 'strong_growth' : growth > 0 ? 'growth' : growth > -5 ? 'stable' : 'decline'
  };
}

// Revenue Analytics with forecasting
const revenueAnalytics = {
  monthly: {
    current: salesData.revenue || 0,
    previous: salesData.previousMonthRevenue || 0,
    yearOverYear: salesData.sameMonthLastYear || 0,
    growth: calculateGrowth(salesData.revenue || 0, salesData.previousMonthRevenue || 0),
    yoyGrowth: calculateGrowth(salesData.revenue || 0, salesData.sameMonthLastYear || 0),
    forecast: salesData.nextMonthForecast || 0,
    target: salesData.monthlyTarget || 0,
    achievement: salesData.monthlyTarget ? ((salesData.revenue / salesData.monthlyTarget) * 100).toFixed(1) : 0
  },
  quarterly: {
    current: salesData.quarterRevenue || 0,
    target: salesData.quarterlyTarget || 0,
    achievement: salesData.quarterlyTarget ? ((salesData.quarterRevenue / salesData.quarterlyTarget) * 100).toFixed(1) : 0
  },
  annual: {
    projection: salesData.annualProjection || 0,
    target: salesData.annualTarget || 0
  }
};

// Advanced Customer Analytics
const customerIntelligence = {
  acquisition: {
    newCustomers: cohortData.newCustomers || 0,
    acquisitionCost: cohortData.acquisitionCost || 0,
    acquisitionTrend: calculateGrowth(cohortData.newCustomers || 0, cohortData.previousNewCustomers || 0)
  },
  retention: {
    cohorts: cohortData.cohorts || [],
    averageRetention: cohortData.averageRetention || 0,
    retentionTrend: calculateGrowth(cohortData.averageRetention || 0, cohortData.previousRetention || 0),
    churnRate: cohortData.churnRate || 0
  },
  value: {
    averageLTV: cohortData.averageLifetimeValue || 0,
    ltvTrend: calculateGrowth(cohortData.averageLifetimeValue || 0, cohortData.previousLTV || 0),
    averageOrderValue: cohortData.averageOrderValue || 0,
    purchaseFrequency: cohortData.purchaseFrequency || 0
  },
  segmentation: {
    highValue: cohortData.segments?.highValue || 0,
    regular: cohortData.segments?.regular || 0,
    atRisk: cohortData.segments?.atRisk || 0,
    lost: cohortData.segments?.lost || 0
  }
};

// Product Portfolio Intelligence
const productIntelligence = {
  performance: {
    topPerformers: productData.topProducts || [],
    underperformers: productData.underperformingProducts || [],
    newProductSuccess: productData.newProductMetrics || {},
    categoryPerformance: productData.categoryAnalysis || []
  },
  inventory: {
    turnoverRate: productData.inventoryTurnover || 0,
    daysOfStock: productData.averageDaysOfStock || 0,
    stockoutRate: productData.stockoutRate || 0,
    excessInventory: productData.excessInventoryValue || 0
  },
  profitability: {
    grossMargin: productData.grossMargin || 0,
    marginalProducts: productData.lowMarginProducts || [],
    profitDrivers: productData.topProfitProducts || []
  },
  lifecycle: {
    introduction: productData.lifecycleStages?.introduction || 0,
    growth: productData.lifecycleStages?.growth || 0,
    maturity: productData.lifecycleStages?.maturity || 0,
    decline: productData.lifecycleStages?.decline || 0
  }
};

// Financial Performance
const financialPerformance = {
  profitability: {
    grossProfit: financialData.grossProfit || 0,
    netProfit: financialData.netProfit || 0,
    grossMargin: financialData.grossMargin || 0,
    netMargin: financialData.netMargin || 0,
    ebitda: financialData.ebitda || 0
  },
  costs: {
    totalOperatingCosts: financialData.operatingCosts || 0,
    marketingCosts: financialData.marketingCosts || 0,
    fulfillmentCosts: financialData.fulfillmentCosts || 0,
    customerServiceCosts: financialData.customerServiceCosts || 0
  },
  roi: {
    marketingROI: financialData.marketingROI || 0,
    overallROI: financialData.overallROI || 0,
    customerAcquisitionROI: financialData.acquisitionROI || 0
  },
  forecasting: {
    nextMonthRevenue: financialData.revenueProjection || 0,
    nextQuarterRevenue: financialData.quarterProjection || 0,
    riskFactors: financialData.riskFactors || []
  }
};

// Market Intelligence
const marketIntelligence = {
  position: {
    marketShare: marketData.marketShare || 0,
    competitiveRank: marketData.competitiveRank || 0,
    brandStrength: marketData.brandStrength || 0
  },
  trends: {
    industryGrowth: marketData.industryGrowth || 0,
    seasonalFactors: marketData.seasonalFactors || [],
    emergingTrends: marketData.emergingTrends || []
  },
  opportunities: {
    marketGaps: marketData.marketGaps || [],
    expansionOpportunities: marketData.expansionOpportunities || [],
    partnershipPotential: marketData.partnershipOpportunities || []
  }
};

// Executive KPIs
const executiveKPIs = {
  revenue: revenueAnalytics.monthly.current,
  revenueGrowth: revenueAnalytics.monthly.growth.percentage,
  customerAcquisition: customerIntelligence.acquisition.newCustomers,
  customerRetention: customerIntelligence.retention.averageRetention,
  averageLTV: customerIntelligence.value.averageLTV,
  grossMargin: financialPerformance.profitability.grossMargin,
  netProfit: financialPerformance.profitability.netProfit,
  marketPosition: marketIntelligence.position.marketShare,
  inventoryEfficiency: productIntelligence.inventory.turnoverRate,
  customerSatisfaction: salesData.customerSatisfactionScore || 0
};

// Strategic Insights
const strategicInsights = [];

// Revenue Insights
if (parseFloat(revenueAnalytics.monthly.growth.percentage) > 20) {
  strategicInsights.push(`üöÄ Exceptional revenue growth: +${revenueAnalytics.monthly.growth.percentage}% - consider scaling operations`);
} else if (parseFloat(revenueAnalytics.monthly.growth.percentage) < -10) {
  strategicInsights.push(`‚ö†Ô∏è Revenue decline detected: ${revenueAnalytics.monthly.growth.percentage}% - immediate strategy review required`);
}

// Customer Insights
if (customerIntelligence.retention.averageRetention < 60) {
  strategicInsights.push(`üîÑ Low customer retention (${customerIntelligence.retention.averageRetention}%) - focus on retention programs`);
}

if (customerIntelligence.segmentation.atRisk > customerIntelligence.segmentation.highValue) {
  strategicInsights.push(`‚ö° High number of at-risk customers - implement retention campaigns`);
}

// Product Insights
if (productIntelligence.inventory.turnoverRate < 6) {
  strategicInsights.push(`üì¶ Low inventory turnover (${productIntelligence.inventory.turnoverRate}x) - optimize inventory management`);
}

if (productIntelligence.profitability.grossMargin < 40) {
  strategicInsights.push(`üí∞ Margin pressure (${productIntelligence.profitability.grossMargin}%) - review pricing strategy`);
}

// Market Insights
if (marketIntelligence.position.marketShare > 0 && marketIntelligence.position.marketShare < 5) {
  strategicInsights.push(`üéØ Market share opportunity (${marketIntelligence.position.marketShare}%) - consider expansion strategy`);
}

// Strategic Recommendations
const strategicRecommendations = [];

if (parseFloat(revenueAnalytics.monthly.achievement) < 85) {
  strategicRecommendations.push('üéØ Revenue target at risk - implement emergency revenue generation tactics');
}

if (customerIntelligence.acquisition.acquisitionCost > customerIntelligence.value.averageLTV * 0.3) {
  strategicRecommendations.push('üí∏ High acquisition cost - optimize marketing efficiency');
}

if (productIntelligence.lifecycle.decline > productIntelligence.lifecycle.introduction) {
  strategicRecommendations.push('üÜï Product portfolio aging - accelerate new product development');
}

if (financialPerformance.profitability.netMargin < 10) {
  strategicRecommendations.push('üíé Improve profitability - focus on high-margin products and cost optimization');
}

return {
  monthlyReport: {
    reportPeriod: {
      month: monthRanges.monthName,
      monthNumber: monthRanges.monthNumber,
      year: monthRanges.year,
      startDate: monthRanges.lastMonth,
      endDate: monthRanges.lastMonthEnd
    },
    revenueAnalytics,
    customerIntelligence,
    productIntelligence,
    financialPerformance,
    marketIntelligence,
    executiveKPIs,
    strategicInsights,
    strategicRecommendations,
    generatedAt: new Date().toISOString()
  }
};

// Generate Executive Monthly Report
const report = $json.monthlyReport;

let message = `üìä **EXECUTIVE MONTHLY REPORT** üìä\n`;
message += `üóìÔ∏è **${report.reportPeriod.month} ${report.reportPeriod.year}**\n\n`;

// Executive Summary
message += `üèÜ **EXECUTIVE SUMMARY**\n`;
message += `‚îú Umsatz: **${report.revenueAnalytics.monthly.current.toLocaleString()}‚Ç¨** (${report.revenueAnalytics.monthly.growth.percentage > 0 ? 'üìà' : 'üìâ'} ${report.revenueAnalytics.monthly.growth.percentage}%)\n`;
message += `‚îú Ziel-Erreichung: **${report.revenueAnalytics.monthly.achievement}%**\n`;
message += `‚îú Neue Kunden: **${report.customerIntelligence.acquisition.newCustomers}**\n`;
message += `‚îú Customer Retention: **${report.customerIntelligence.retention.averageRetention}%**\n`;
message += `‚îî Bruttogewinn: **${report.financialPerformance.profitability.grossMargin}%**\n\n`;

// Revenue Performance
message += `üí∞ **REVENUE ANALYTICS**\n`;
message += `‚îú Monatsumsatz: **${report.revenueAnalytics.monthly.current.toLocaleString()}‚Ç¨**\n`;
message += `‚îú MoM Wachstum: **${report.revenueAnalytics.monthly.growth.percentage}%**\n`;
message += `‚îú YoY Wachstum: **${report.revenueAnalytics.monthly.yoyGrowth.percentage}%**\n`;
message += `‚îú Q-Ziel: **${report.revenueAnalytics.quarterly.achievement}%**\n`;
message += `‚îî Prognose n√§chster Monat: **${report.revenueAnalytics.monthly.forecast.toLocaleString()}‚Ç¨**\n\n`;

// Customer Intelligence
message += `üë• **CUSTOMER INTELLIGENCE**\n`;
message += `‚îú Akquisition: **${report.customerIntelligence.acquisition.newCustomers}** (Kosten: ${report.customerIntelligence.acquisition.acquisitionCost.toFixed(2)}‚Ç¨)\n`;
message += `‚îú √ò LTV: **${report.customerIntelligence.value.averageLTV.toFixed(2)}‚Ç¨** (${report.customerIntelligence.value.ltvTrend.percentage > 0 ? 'üìà' : 'üìâ'} ${report.customerIntelligence.value.ltvTrend.percentage}%)\n`;
message += `‚îú High-Value: **${report.customerIntelligence.segmentation.highValue}** | At-Risk: **${report.customerIntelligence.segmentation.atRisk}**\n`;
message += `‚îî Churn Rate: **${report.customerIntelligence.retention.churnRate}%**\n\n`;

// Product Performance
if (report.productIntelligence.performance.topPerformers.length > 0) {
  message += `üèÜ **TOP PRODUCTS**\n`;
  report.productIntelligence.performance.topPerformers.slice(0, 3).forEach((product, index) => {
    const emoji = index === 0 ? 'ü•á' : index === 1 ? 'ü•à' : 'ü•â';
    message += `${emoji} ${product.name}: **${product.revenue.toLocaleString()}‚Ç¨** (${product.margin}% Marge)\n`;
  });
  message += `\n`;
}

// Financial Performance
message += `üíé **FINANCIAL PERFORMANCE**\n`;
message += `‚îú Bruttogewinn: **${report.financialPerformance.profitability.grossProfit.toLocaleString()}‚Ç¨** (${report.financialPerformance.profitability.grossMargin}%)\n`;
message += `‚îú Nettogewinn: **${report.financialPerformance.profitability.netProfit.toLocaleString()}‚Ç¨** (${report.financialPerformance.profitability.netMargin}%)\n`;
message += `‚îú Marketing ROI: **${report.financialPerformance.roi.marketingROI}%**\n`;
message += `‚îî Betriebskosten: **${report.financialPerformance.costs.totalOperatingCosts.toLocaleString()}‚Ç¨**\n\n`;

// Strategic Insights
if (report.strategicInsights.length > 0) {
  message += `üí° **STRATEGIC INSIGHTS**\n`;
  report.strategicInsights.slice(0, 3).forEach(insight => {
    message += `‚Ä¢ ${insight}\n`;
  });
  message += `\n`;
}

// Strategic Recommendations
if (report.strategicRecommendations.length > 0) {
  message += `üéØ **STRATEGIC ACTIONS**\n`;
  report.strategicRecommendations.slice(0, 3).forEach(rec => {
    message += `‚Ä¢ ${rec}\n`;
  });
  message += `\n`;
}

message += `üìä [Executive Dashboard](${$env.DRESSFORP_FRONTEND_URL}/admin/analytics/monthly)\n\n`;
message += `*Monthly Executive Business Intelligence Report*`;

return { monthlyMessage: message };
