// DSGVO Compliance Analysis & Automation
const retentionData = $input.all()[1].json;
const consentData = $input.all()[2].json;
const deletionRequests = $input.all()[3].json;
const auditData = $input.all()[4].json;
const retentionConfig = $input.all()[0].json.retentionConfig;

// Data Retention Analysis
const dataRetentionAnalysis = {
  expiredData: {
    customerRecords: retentionData.expiredCustomers || [],
    orderRecords: retentionData.expiredOrders || [],
    marketingData: retentionData.expiredMarketing || [],
    analyticsData: retentionData.expiredAnalytics || [],
    websiteLogs: retentionData.expiredLogs || []
  },
  totalExpiredRecords: (retentionData.expiredCustomers?.length || 0) + 
                      (retentionData.expiredOrders?.length || 0) + 
                      (retentionData.expiredMarketing?.length || 0) + 
                      (retentionData.expiredAnalytics?.length || 0) + 
                      (retentionData.expiredLogs?.length || 0),
  dataVolumeToDelete: retentionData.totalDataVolumeGB || 0,
  estimatedDeletionTime: retentionData.estimatedDeletionTimeMinutes || 0
};

// Consent Management Analysis
const consentAnalysis = {
  expiredConsents: {
    cookieConsents: consentData.expiredCookieConsents || [],
    marketingConsents: consentData.expiredMarketingConsents || [],
    newsletterConsents: consentData.expiredNewsletterConsents || []
  },
  renewalRequired: {
    total: (consentData.expiredCookieConsents?.length || 0) + 
           (consentData.expiredMarketingConsents?.length || 0) + 
           (consentData.expiredNewsletterConsents?.length || 0),
    critical: consentData.criticalConsentRenewals || []
  },
  consentRates: {
    cookie: consentData.cookieConsentRate || 0,
    marketing: consentData.marketingConsentRate || 0,
    newsletter: consentData.newsletterConsentRate || 0
  }
};

// Customer Deletion Requests
const deletionRequestsAnalysis = {
  pendingRequests: deletionRequests.pending || [],
  totalPending: deletionRequests.pending?.length || 0,
  overdueRequests: deletionRequests.overdue || [],
  processingTime: {
    average: deletionRequests.averageProcessingDays || 0,
    longest: deletionRequests.longestPendingDays || 0
  },
  requestTypes: {
    fullDeletion: deletionRequests.fullDeletionRequests || 0,
    partialDeletion: deletionRequests.partialDeletionRequests || 0,
    dataPortability: deletionRequests.dataPortabilityRequests || 0
  }
};

// Data Processing Audit
const auditResults = {
  complianceScore: auditData.overallComplianceScore || 0,
  violations: {
    critical: auditData.criticalViolations || [],
    warning: auditData.warningViolations || [],
    info: auditData.infoViolations || []
  },
  dataFlows: {
    documented: auditData.documentedDataFlows || 0,
    undocumented: auditData.undocumentedDataFlows || 0,
    thirdPartySharing: auditData.thirdPartyDataSharing || []
  },
  securityMeasures: {
    encryption: auditData.encryptionCompliance || 0,
    accessControl: auditData.accessControlCompliance || 0,
    dataMinimization: auditData.dataMinimizationScore || 0
  }
};

// Automated Actions Required
const automatedActions = [];

// Data Deletion Actions
if (dataRetentionAnalysis.totalExpiredRecords > 0) {
  automatedActions.push({
    type: 'data_deletion',
    priority: 'high',
    action: 'schedule_automatic_deletion',
    details: {
      recordsToDelete: dataRetentionAnalysis.totalExpiredRecords,
      estimatedTime: dataRetentionAnalysis.estimatedDeletionTime,
      dataVolume: dataRetentionAnalysis.dataVolumeToDelete
    }
  });
}

// Consent Renewal Actions
if (consentAnalysis.renewalRequired.total > 0) {
  automatedActions.push({
    type: 'consent_renewal',
    priority: 'medium',
    action: 'send_consent_renewal_emails',
    details: {
      totalUsers: consentAnalysis.renewalRequired.total,
      criticalRenewals: consentAnalysis.renewalRequired.critical.length
    }
  });
}

// Deletion Request Processing
if (deletionRequestsAnalysis.overdueRequests.length > 0) {
  automatedActions.push({
    type: 'urgent_deletion_processing',
    priority: 'critical',
    action: 'process_overdue_deletion_requests',
    details: {
      overdueRequests: deletionRequestsAnalysis.overdueRequests.length,
      longestPending: deletionRequestsAnalysis.processingTime.longest
    }
  });
}

// Compliance Violations
if (auditResults.violations.critical.length > 0) {
  automatedActions.push({
    type: 'compliance_violation',
    priority: 'critical',
    action: 'immediate_violation_remediation',
    details: {
      criticalViolations: auditResults.violations.critical.length,
      complianceScore: auditResults.complianceScore
    }
  });
}

// Compliance Recommendations
const complianceRecommendations = [];

if (auditResults.complianceScore < 85) {
  complianceRecommendations.push('üö® Compliance-Score unter 85% - sofortige Ma√ünahmen erforderlich');
}

if (consentAnalysis.consentRates.cookie < 70) {
  complianceRecommendations.push('üç™ Niedrige Cookie-Consent-Rate - Cookie-Banner optimieren');
}

if (deletionRequestsAnalysis.processingTime.average > 25) {
  complianceRecommendations.push('‚è±Ô∏è L√∂sch-Anfragen-Bearbeitung zu langsam - Prozess automatisieren');
}

if (auditResults.dataFlows.undocumented > 0) {
  complianceRecommendations.push('üìã Undokumentierte Datenfl√ºsse - Verfahrensverzeichnis aktualisieren');
}

// Risk Assessment
const riskAssessment = {
  overall: 'low',
  factors: []
};

if (auditResults.violations.critical.length > 0) {
  riskAssessment.overall = 'critical';
  riskAssessment.factors.push('Kritische Compliance-Verletzungen');
}

if (deletionRequestsAnalysis.overdueRequests.length > 5) {
  riskAssessment.overall = riskAssessment.overall === 'critical' ? 'critical' : 'high';
  riskAssessment.factors.push('√úberf√§llige L√∂sch-Anfragen');
}

if (dataRetentionAnalysis.totalExpiredRecords > 10000) {
  riskAssessment.overall = riskAssessment.overall === 'critical' ? 'critical' : 'medium';
  riskAssessment.factors.push('Hohe Anzahl abgelaufener Daten');
}

return {
  dsgvoCompliance: {
    checkDate: retentionConfig.checkDate,
    dataRetentionAnalysis,
    consentAnalysis,
    deletionRequestsAnalysis,
    auditResults,
    automatedActions,
    complianceRecommendations,
    riskAssessment,
    generatedAt: new Date().toISOString()
  }
};

// Generate DSGVO Compliance Report Message
const compliance = $json.dsgvoCompliance;

let message = `üõ°Ô∏è **DSGVO COMPLIANCE REPORT** üõ°Ô∏è\n`;
message += `üìÖ **${DateTime.fromISO(compliance.checkDate).toFormat('dd.MM.yyyy')}**\n\n`;

// Risk Assessment
const riskEmoji = {
  low: '‚úÖ',
  medium: '‚ö†Ô∏è',
  high: 'üö®',
  critical: 'üî¥'
};

message += `üéØ **COMPLIANCE STATUS**\n`;
message += `‚îú Risk Level: ${riskEmoji[compliance.riskAssessment.overall]} **${compliance.riskAssessment.overall.toUpperCase()}**\n`;
message += `‚îú Compliance Score: **${compliance.auditResults.complianceScore}%**\n`;
message += `‚îî Violations: **${compliance.auditResults.violations.critical.length}** kritisch, **${compliance.auditResults.violations.warning.length}** Warnungen\n\n`;

// Data Retention
message += `üóÇÔ∏è **DATENAUFBEWAHRUNG**\n`;
message += `‚îú Abgelaufene Datens√§tze: **${compliance.dataRetentionAnalysis.totalExpiredRecords}**\n`;
message += `‚îú Datenvolumen zu l√∂schen: **${compliance.dataRetentionAnalysis.dataVolumeToDelete} GB**\n`;
message += `‚îî Gesch√§tzte L√∂schzeit: **${compliance.dataRetentionAnalysis.estimatedDeletionTime} Min**\n\n`;

// Consent Management
message += `üìã **EINVERST√ÑNDNIS-MANAGEMENT**\n`;
message += `‚îú Erneuerung erforderlich: **${compliance.consentAnalysis.renewalRequired.total}**\n`;
message += `‚îú Cookie-Consent-Rate: **${compliance.consentAnalysis.consentRates.cookie}%**\n`;
message += `‚îú Marketing-Consent-Rate: **${compliance.consentAnalysis.consentRates.marketing}%**\n`;
message += `‚îî Newsletter-Consent-Rate: **${compliance.consentAnalysis.consentRates.newsletter}%**\n\n`;

// Deletion Requests
if (compliance.deletionRequestsAnalysis.totalPending > 0) {
  message += `üóëÔ∏è **L√ñSCH-ANFRAGEN**\n`;
  message += `‚îú Ausstehend: **${compliance.deletionRequestsAnalysis.totalPending}**\n`;
  message += `‚îú √úberf√§llig: **${compliance.deletionRequestsAnalysis.overdueRequests.length}**\n`;
  message += `‚îî √ò Bearbeitungszeit: **${compliance.deletionRequestsAnalysis.processingTime.average} Tage**\n\n`;
}

// Automated Actions
if (compliance.automatedActions.length > 0) {
  message += `ü§ñ **AUTOMATISCHE AKTIONEN**\n`;
  compliance.automatedActions.forEach(action => {
    const priorityEmoji = {
      critical: 'üî¥',
      high: 'üü†',
      medium: 'üü°',
      low: 'üü¢'
    };
    message += `${priorityEmoji[action.priority]} ${action.action.replace(/_/g, ' ')}\n`;
  });
  message += `\n`;
}

// Recommendations
if (compliance.complianceRecommendations.length > 0) {
  message += `üí° **COMPLIANCE-EMPFEHLUNGEN**\n`;
  compliance.complianceRecommendations.slice(0, 3).forEach(rec => {
    message += `‚Ä¢ ${rec}\n`;
  });
  message += `\n`;
}

// Risk Factors
if (compliance.riskAssessment.factors.length > 0) {
  message += `‚ö†Ô∏è **RISIKOFAKTOREN**\n`;
  compliance.riskAssessment.factors.forEach(factor => {
    message += `‚Ä¢ ${factor}\n`;
  });
  message += `\n`;
}

message += `üõ°Ô∏è [DSGVO Dashboard](${$env.DRESSFORP_FRONTEND_URL}/admin/compliance)\n\n`;
message += `*DSGVO Compliance Automation Report*`;

return { complianceMessage: message };
