// Validate and enhance cart abandonment data
const inputData = $input.first().json;

// For webhook trigger
if (inputData.cartId || inputData.cart_id) {
  const cartData = {
    cartId: inputData.cartId || inputData.cart_id,
    customerId: inputData.customerId || inputData.customer_id,
    customerEmail: inputData.customerEmail || inputData.customer_email,
    customerName: inputData.customerName || inputData.customer_name || 'Valued Customer',
    items: inputData.items || [],
    totalValue: inputData.totalValue || inputData.total_value || 0,
    currency: inputData.currency || 'EUR',
    abandonedAt: inputData.abandonedAt || inputData.abandoned_at || new Date().toISOString(),
    sessionDuration: inputData.sessionDuration || inputData.session_duration || 0,
    source: inputData.source || 'website',
    deviceType: inputData.deviceType || inputData.device_type || 'unknown',
    userAgent: inputData.userAgent || inputData.user_agent || '',
    checkoutStep: inputData.checkoutStep || inputData.checkout_step || 'cart'
  };

  return { 
    cartData,
    triggerType: 'webhook',
    isValidCart: cartData.cartId && cartData.customerEmail && cartData.items.length > 0
  };
}

// For scheduled trigger - signal to fetch abandoned carts
return {
  triggerType: 'scheduled',
  fetchAbandonedCarts: true,
  checkTime: new Date().toISOString()
};

// Determine cart recovery strategy based on customer data and cart value
const cartData = $input.all()[0].json.cartData || $input.all()[0].json;
const eligibilityData = $input.all()[1].json;

// Customer analysis
const customerTier = eligibilityData.customerTier || 'standard';
const previousOrders = eligibilityData.previousOrders || 0;
const averageOrderValue = eligibilityData.averageOrderValue || 0;
const lastOrderDays = eligibilityData.daysSinceLastOrder || 999;
const recoveryAttempts = eligibilityData.previousRecoveryAttempts || 0;

// Cart analysis
const cartValue = cartData.totalValue || 0;
const itemCount = cartData.items?.length || 0;
const abandonedHours = Math.abs(new Date() - new Date(cartData.abandonedAt)) / (1000 * 60 * 60);

// Strategy determination
let strategy = {
  type: 'standard',
  urgency: 'medium',
  incentive: 'none',
  incentiveValue: 0,
  personalizedMessage: false,
  followUpSequence: false,
  waitTime: 1, // hours
  maxAttempts: 2
};

// High-value cart strategy
if (cartValue >= 100) {
  strategy.type = 'high_value';
  strategy.urgency = 'high';
  strategy.incentive = 'discount';
  strategy.incentiveValue = cartValue >= 200 ? 15 : 10;
  strategy.personalizedMessage = true;
  strategy.followUpSequence = true;
  strategy.waitTime = 0.5;
  strategy.maxAttempts = 3;
}

// VIP customer strategy
if (customerTier === 'vip' || customerTier === 'premium') {
  strategy.urgency = 'high';
  strategy.incentive = 'discount';
  strategy.incentiveValue = Math.max(strategy.incentiveValue, 15);
  strategy.personalizedMessage = true;
  strategy.followUpSequence = true;
  strategy.maxAttempts = 4;
}

// New customer strategy
if (previousOrders === 0) {
  strategy.type = 'new_customer';
  strategy.incentive = 'welcome_discount';
  strategy.incentiveValue = 10;
  strategy.personalizedMessage = true;
  strategy.maxAttempts = 3;
}

// Returning customer strategy
if (previousOrders > 0 && lastOrderDays > 90) {
  strategy.type = 'win_back';
  strategy.incentive = 'special_offer';
  strategy.incentiveValue = 12;
  strategy.personalizedMessage = true;
  strategy.followUpSequence = true;
}

// Quick abandonment (< 2 hours) - immediate recovery
if (abandonedHours < 2) {
  strategy.waitTime = 0.25; // 15 minutes
  strategy.urgency = 'immediate';
}

// Multiple items - bundle offer
if (itemCount >= 3) {
  strategy.incentive = cartValue >= 80 ? 'free_shipping' : 'bundle_discount';
  strategy.incentiveValue = strategy.incentive === 'bundle_discount' ? 8 : 0;
}

// Limit recovery attempts
if (recoveryAttempts >= strategy.maxAttempts) {
  return {
    skipRecovery: true,
    reason: 'max_attempts_reached',
    attempts: recoveryAttempts
  };
}

// Low-value cart filter
if (cartValue < 15 && customerTier === 'standard') {
  return {
    skipRecovery: true,
    reason: 'low_value_cart',
    cartValue: cartValue
  };
}

// Generate recovery token
const recoveryToken = 'cart_' + cartData.cartId + '_' + Date.now();

// Product recommendations
const productRecommendations = cartData.items?.map(item => ({
  id: item.productId,
  name: item.name,
  price: item.price,
  image: item.image
})) || [];

return {
  recoveryStrategy: strategy,
  cartData: cartData,
  customerData: {
    tier: customerTier,
    previousOrders: previousOrders,
    averageOrderValue: averageOrderValue,
    lastOrderDays: lastOrderDays
  },
  recoveryToken: recoveryToken,
  productRecommendations: productRecommendations,
  skipRecovery: false,
  scheduledSendTime: new Date(Date.now() + (strategy.waitTime * 60 * 60 * 1000)).toISOString()
};

// Generate personalized cart recovery email content
const strategy = $json.recoveryStrategy;
const cartData = $json.cartData;
const customerData = $json.customerData;
const recoveryToken = $json.recoveryToken;
const recommendations = $json.productRecommendations;

// Personalization based on customer tier and cart value
const customerName = cartData.customerName || 'Liebe Kundin, lieber Kunde';
const isVIP = customerData.tier === 'vip' || customerData.tier === 'premium';
const isNewCustomer = customerData.previousOrders === 0;

// Subject line generation
let subject = '';
if (strategy.urgency === 'immediate') {
  subject = isVIP ? `${customerName}, Ihr VIP-Warenkorb wartet noch...` : 
           `Vergessen? Ihr Warenkorb (${cartData.totalValue.toFixed(2)}‚Ç¨) wartet!`;
} else if (strategy.type === 'high_value') {
  subject = `Nur noch wenige Stunden: ${cartData.totalValue.toFixed(2)}‚Ç¨ Warenkorb + ${strategy.incentiveValue}% Rabatt`;
} else if (strategy.type === 'new_customer') {
  subject = `Willkommen! Ihr Warenkorb + ${strategy.incentiveValue}% Willkommensrabatt`;
} else if (strategy.type === 'win_back') {
  subject = `Wir vermissen Sie! Ihr Warenkorb + ${strategy.incentiveValue}% Wiedersehensrabatt`;
} else {
  subject = `Ihr Warenkorb wartet noch (${cartData.totalValue.toFixed(2)}‚Ç¨)`;
}

// Greeting and opening
let greeting = '';
if (isVIP) {
  greeting = `Liebe${customerName.includes(' ') ? 'r' : ''} ${customerName.split(' ')[0]},\n\nals gesch√§tzte${customerName.includes(' ') ? 'r' : ''} VIP-Kunde${customerName.includes(' ') ? '' : 'in'} wollten wir Sie daran erinnern...`;
} else if (isNewCustomer) {
  greeting = `Hallo und herzlich willkommen bei DressForPleasure!\n\nWir freuen uns, dass Sie sich f√ºr uns entschieden haben...`;
} else {
  greeting = `Hallo ${customerName.split(' ')[0]},\n\nIhr Warenkorb wartet noch auf Sie...`;
}

// Main message based on strategy
let mainMessage = '';
if (strategy.type === 'high_value') {
  mainMessage = `Sie haben Artikel im Wert von **${cartData.totalValue.toFixed(2)}‚Ç¨** ausgew√§hlt. Das ist eine gro√üartige Auswahl! \n\nAls kleines Dankesch√∂n f√ºr Ihr Vertrauen erhalten Sie **${strategy.incentiveValue}% Rabatt** auf Ihre gesamte Bestellung.`;
} else if (strategy.type === 'new_customer') {
  mainMessage = `Wir m√∂chten Ihnen den Einstieg erleichtern und schenken Ihnen **${strategy.incentiveValue}% Willkommensrabatt** auf Ihre erste Bestellung!`;
} else if (strategy.type === 'win_back') {
  mainMessage = `Sch√∂n, dass Sie wieder da sind! Als Wiedersehensgru√ü erhalten Sie **${strategy.incentiveValue}% Rabatt** auf Ihren Warenkorb.`;
} else {
  mainMessage = `Ihre ausgew√§hlten Artikel warten noch in Ihrem Warenkorb. Schlie√üen Sie Ihre Bestellung ab, bevor die beliebten Artikel vergriffen sind!`;
}

// Urgency and scarcity elements
let urgencyMessage = '';
if (strategy.urgency === 'high' || strategy.urgency === 'immediate') {
  urgencyMessage = `‚è∞ **Nur noch begrenzte Zeit verf√ºgbar!** Einzelne Artikel in Ihrem Warenkorb sind sehr beliebt und k√∂nnten bald ausverkauft sein.`;
}

// Incentive details
let incentiveDetails = '';
if (strategy.incentive !== 'none') {
  const incentiveMap = {
    'discount': `${strategy.incentiveValue}% Rabatt auf alles`,
    'welcome_discount': `${strategy.incentiveValue}% Willkommensrabatt`,
    'special_offer': `${strategy.incentiveValue}% Sonderrabatt`,
    'free_shipping': 'Kostenloser Versand',
    'bundle_discount': `${strategy.incentiveValue}% Bundle-Rabatt`
  };

  const incentiveText = incentiveMap[strategy.incentive] || `${strategy.incentiveValue}% Rabatt`;
  incentiveDetails = `\n\nüéÅ **Ihr Vorteil: ${incentiveText}**\nCode: CART${strategy.incentiveValue || 'SAVE'}`;
}

// Product list
let productList = '';
if (recommendations.length > 0) {
  productList = '\n\n**Ihre ausgew√§hlten Artikel:**\n';
  recommendations.forEach(product => {
    productList += `‚Ä¢ ${product.name} - ${product.price.toFixed(2)}‚Ç¨\n`;
  });
}

// Social proof for high-value customers
let socialProof = '';
if (strategy.type === 'high_value' || isVIP) {
  socialProof = '\n\n‚≠ê "Fantastische Qualit√§t und super Service!" - Bewertung eines zufriedenen Kunden\n‚úÖ √úber 10.000 gl√ºckliche Kunden vertrauen bereits auf DressForPleasure';
}

// Security and trust signals
const trustSignals = '\n\nüîí Sicherer Checkout | üì¶ Kostenloser Versand ab 50‚Ç¨ | üîÑ 30 Tage R√ºckgaberecht';

// Recovery link
const recoveryUrl = `${process.env.DRESSFORP_FRONTEND_URL}/cart/recover?token=${recoveryToken}`;

// Call-to-action
let ctaText = '';
if (strategy.urgency === 'immediate') {
  ctaText = 'Jetzt sichern';
} else if (strategy.incentive !== 'none') {
  ctaText = 'Rabatt einl√∂sen';
} else {
  ctaText = 'Bestellung abschlie√üen';
}

return {
  emailContent: {
    subject: subject,
    greeting: greeting,
    mainMessage: mainMessage,
    urgencyMessage: urgencyMessage,
    incentiveDetails: incentiveDetails,
    productList: productList,
    socialProof: socialProof,
    trustSignals: trustSignals,
    recoveryUrl: recoveryUrl,
    ctaText: ctaText,
    customerTier: customerData.tier,
    cartValue: cartData.totalValue,
    incentiveCode: strategy.incentive !== 'none' ? `CART${strategy.incentiveValue || 'SAVE'}` : null
  },
  trackingData: {
    cartId: cartData.cartId,
    customerId: cartData.customerId,
    strategy: strategy.type,
    incentiveValue: strategy.incentiveValue,
    attempt: (customerData.recoveryAttempts || 0) + 1
  }
};
